/* ============================================
   Variables CSS - Palette de couleurs
   ============================================ */
:root {
    --color-bg-main: #FDFCF6;
    --color-gold: #D4AF37;
    --color-text-dark: #1A1A2E;
    --color-cta-amber: #FFB347;
    --color-footer-navy: #0B1D3F;
    --color-white: #FFFFFF;
    --color-shadow: rgba(0, 0, 0, 0.1);
    --color-overlay: rgba(11, 29, 63, 0.7);

    --font-primary: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    --transition-smooth: all 0.3s ease;
    --shadow-card: 0 4px 12px rgba(0, 0, 0, 0.08);
    --shadow-hover: 0 8px 24px rgba(0, 0, 0, 0.15);
}

/* ============================================
   Reset & Base
   ============================================ */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    scroll-behavior: smooth;
}

body {
    font-family: var(--font-primary);
    background-color: var(--color-bg-main);
    color: var(--color-text-dark);
    line-height: 1.6;
    overflow-x: hidden;
}

img {
    max-width: 100%;
    height: auto;
    display: block;
}

a {
    text-decoration: none;
    color: inherit;
}

/* ============================================
   Animations CSS
   ============================================ */
@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes pulse {
    0%, 100% {
        transform: scale(1);
    }
    50% {
        transform: scale(1.05);
    }
}

@keyframes glow {
    0%, 100% {
        box-shadow: 0 0 10px rgba(255, 179, 71, 0.5);
    }
    50% {
        box-shadow: 0 0 20px rgba(255, 179, 71, 0.8), 0 0 30px rgba(212, 175, 55, 0.4);
    }
}

.fade-in {
    animation: fadeIn 1s ease-out forwards;
}

.fade-in-delay {
    opacity: 0;
    animation: fadeIn 1s ease-out 0.3s forwards;
}

.fade-in-delay-2 {
    opacity: 0;
    animation: fadeIn 1s ease-out 0.6s forwards;
}

.fade-in-delay-3 {
    opacity: 0;
    animation: fadeIn 1s ease-out 0.9s forwards;
}

.fade-in-delay-4 {
    opacity: 0;
    animation: fadeIn 1s ease-out 1.2s forwards;
}

/* Animation fade-in pour les images générées */
.fade-in-img {
    opacity: 0;
    animation: fadeIn 0.8s ease-out forwards;
}

/* ============================================
   Container & Layout
   ============================================ */
.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

/* ============================================
   Header
   ============================================ */
.header {
    background-color: var(--color-white);
    box-shadow: 0 2px 8px var(--color-shadow);
    padding: 15px 0;
    position: sticky;
    top: 0;
    z-index: 100;
}

.header-content {
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 15px;
}

.logo-wrapper {
    display: flex;
    align-items: center;
    gap: 15px;
}

.logo {
    font-size: 1.5rem;
    font-weight: bold;
    color: var(--color-gold);
}

.header-astrology-img {
    height: 50px;
    width: auto;
    opacity: 0.85;
    transition: var(--transition-smooth);
}

.header-astrology-img:hover {
    opacity: 1;
    transform: scale(1.05);
}

/* ============================================
   Boutons
   ============================================ */
.btn {
    display: inline-block;
    padding: 12px 28px;
    border-radius: 50px;
    font-weight: 600;
    text-align: center;
    transition: var(--transition-smooth);
    cursor: pointer;
    border: none;
    font-size: 1rem;
}

.btn-primary {
    background-color: var(--color-cta-amber);
    color: var(--color-white);
}

.btn-primary:hover {
    background-color: #ff9d2e;
    transform: translateY(-2px);
    box-shadow: var(--shadow-hover);
    animation: glow 2s infinite;
}

.btn-secondary {
    background-color: var(--color-gold);
    color: var(--color-white);
}

.btn-secondary:hover {
    background-color: #c9a22e;
    transform: translateY(-2px);
}

.btn-cta {
    background: linear-gradient(135deg, var(--color-cta-amber) 0%, #ff9d2e 100%);
    color: var(--color-white);
    box-shadow: 0 4px 15px rgba(255, 179, 71, 0.3);
}

.btn-cta:hover {
    transform: translateY(-3px) scale(1.02);
    box-shadow: 0 8px 25px rgba(255, 179, 71, 0.5);
    animation: glow 1.5s infinite;
}

.btn-large {
    padding: 16px 40px;
    font-size: 1.1rem;
}

/* ============================================
   Hero Section
   ============================================ */
.hero {
    position: relative;
    background: linear-gradient(135deg, var(--color-footer-navy) 0%, #1a3a6e 100%);
    background-image: url('assets/hero.png');
    background-size: cover;
    background-position: center;
    background-attachment: fixed;
    min-height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    text-align: center;
    padding: 80px 20px;
}

.hero-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: var(--color-overlay);
    z-index: 1;
}

.hero-content {
    position: relative;
    z-index: 2;
    max-width: 800px;
}

.hero-title {
    font-size: 2.5rem;
    line-height: 1.2;
    color: var(--color-white);
    margin-bottom: 20px;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
}

.hero-subtitle {
    font-size: 1.8rem;
    color: var(--color-gold);
    margin-bottom: 15px;
    font-weight: 600;
}

.hero-description {
    font-size: 1.2rem;
    color: var(--color-white);
    margin-bottom: 35px;
    opacity: 0.95;
}

.trust-badge {
    display: inline-block;
    background-color: rgba(255, 255, 255, 0.15);
    backdrop-filter: blur(10px);
    padding: 12px 24px;
    border-radius: 50px;
    color: var(--color-white);
    font-size: 0.95rem;
    margin-top: 20px;
}

/* ============================================
   À Propos Section
   ============================================ */
.about {
    padding: 80px 20px;
    background-color: var(--color-white);
}

.about-content {
    display: grid;
    grid-template-columns: 1fr 2fr;
    gap: 40px;
    align-items: center;
}

.about-image .portrait {
    border-radius: 15px;
    box-shadow: var(--shadow-card);
    width: 100%;
}

.about-text p {
    margin-bottom: 20px;
    line-height: 1.8;
    font-size: 1.05rem;
}

.about-text .highlight {
    background: linear-gradient(135deg, #fff8e7 0%, #ffe4b3 100%);
    padding: 20px;
    border-left: 4px solid var(--color-gold);
    border-radius: 8px;
    font-style: italic;
    font-size: 1.1rem;
    margin-top: 30px;
}

/* ============================================
   Sections Générales
   ============================================ */
section {
    padding: 60px 20px;
}

.section-title {
    font-size: 2.2rem;
    color: var(--color-text-dark);
    margin-bottom: 15px;
    position: relative;
}

.section-title.center {
    text-align: center;
}

.section-title.center::after {
    content: '';
    display: block;
    width: 80px;
    height: 4px;
    background: linear-gradient(90deg, var(--color-gold) 0%, var(--color-cta-amber) 100%);
    margin: 15px auto 0;
    border-radius: 2px;
}

.section-subtitle {
    text-align: center;
    font-size: 1.15rem;
    color: #666;
    margin-bottom: 50px;
}

/* ============================================
   Pourquoi Choisir Section
   ============================================ */
.why-choose {
    background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
}

.features-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 30px;
    margin-top: 40px;
}

.feature-card {
    background-color: var(--color-white);
    padding: 30px;
    border-radius: 12px;
    box-shadow: var(--shadow-card);
    text-align: center;
    transition: var(--transition-smooth);
}

.feature-card:hover {
    transform: translateY(-8px);
    box-shadow: var(--shadow-hover);
}

.feature-icon {
    font-size: 3rem;
    margin-bottom: 15px;
}

.feature-card h3 {
    font-size: 1.3rem;
    color: var(--color-text-dark);
    margin-bottom: 12px;
}

.feature-card p {
    color: #666;
    line-height: 1.6;
}

/* ============================================
   Services Section
   ============================================ */
.services {
    background-color: var(--color-white);
}

.services-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 30px;
    margin-top: 40px;
}

.service-card {
    background: linear-gradient(135deg, #ffffff 0%, #f8f9fa 100%);
    padding: 35px;
    border-radius: 15px;
    box-shadow: var(--shadow-card);
    border: 2px solid transparent;
    transition: var(--transition-smooth);
    text-align: center;
}

.service-card:hover {
    border-color: var(--color-gold);
    transform: translateY(-5px);
    box-shadow: var(--shadow-hover);
}

.service-img {
    width: 100%;
    height: 200px;
    object-fit: cover;
    border-radius: 10px;
    margin-bottom: 20px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
}

.service-icon {
    font-size: 3.5rem;
    margin-bottom: 20px;
}

.service-card h3 {
    font-size: 1.5rem;
    color: var(--color-text-dark);
    margin-bottom: 15px;
}

.service-card p {
    color: #555;
    line-height: 1.7;
    margin-bottom: 25px;
}

/* ============================================
   Comment ça marche Section
   ============================================ */
.how-it-works {
    background: linear-gradient(135deg, #0B1D3F 0%, #1a3a6e 100%);
    color: var(--color-white);
}

.how-it-works .section-title,
.how-it-works .section-subtitle {
    color: var(--color-white);
}

.steps {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 20px;
    margin: 50px 0;
    flex-wrap: wrap;
}

.step {
    flex: 1;
    min-width: 200px;
    text-align: center;
}

.step-number {
    width: 60px;
    height: 60px;
    background: linear-gradient(135deg, var(--color-gold) 0%, var(--color-cta-amber) 100%);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.8rem;
    font-weight: bold;
    margin: 0 auto 20px;
    color: var(--color-white);
}

.step h3 {
    font-size: 1.2rem;
    margin-bottom: 10px;
}

.step p {
    font-size: 0.95rem;
    opacity: 0.9;
}

.step-arrow {
    font-size: 2rem;
    color: var(--color-gold);
}

.cta-center {
    text-align: center;
    margin-top: 40px;
}

.reassurance-text {
    font-size: 1.1rem;
    margin-bottom: 20px;
    color: var(--color-white);
}

/* ============================================
   Témoignages Section
   ============================================ */
.testimonials {
    background-color: var(--color-bg-main);
}

.testimonials-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
    gap: 30px;
    margin-top: 40px;
}

.testimonial-card {
    background-color: var(--color-white);
    padding: 30px;
    border-radius: 12px;
    box-shadow: var(--shadow-card);
    transition: var(--transition-smooth);
}

.testimonial-card:hover {
    transform: translateY(-5px);
    box-shadow: var(--shadow-hover);
}

.testimonial-avatar {
    width: 80px;
    height: 80px;
    border-radius: 50%;
    margin: 0 auto 20px;
    border: 3px solid var(--color-gold);
    object-fit: cover;
}

.testimonial-text {
    font-style: italic;
    color: #555;
    line-height: 1.7;
    margin-bottom: 20px;
    position: relative;
}

.testimonial-text::before {
    content: '"';
    font-size: 3rem;
    color: var(--color-gold);
    position: absolute;
    top: -15px;
    left: -10px;
    opacity: 0.3;
}

.testimonial-author {
    font-weight: 600;
    color: var(--color-text-dark);
    margin-bottom: 8px;
}

.testimonial-problem {
    font-size: 0.9rem;
    color: #888;
    font-style: italic;
}

/* ============================================
   FAQ Section
   ============================================ */
.faq {
    background-color: var(--color-white);
}

.faq-list {
    max-width: 900px;
    margin: 40px auto 0;
}

.faq-item {
    background: linear-gradient(135deg, #f8f9fa 0%, #ffffff 100%);
    padding: 25px;
    border-radius: 10px;
    margin-bottom: 20px;
    box-shadow: var(--shadow-card);
    transition: var(--transition-smooth);
}

.faq-item:hover {
    box-shadow: var(--shadow-hover);
    transform: translateX(5px);
}

.faq-question {
    font-size: 1.2rem;
    color: var(--color-text-dark);
    margin-bottom: 12px;
    font-weight: 600;
}

.faq-answer {
    color: #555;
    line-height: 1.7;
}

/* ============================================
   CTA Final Section
   ============================================ */
.final-cta {
    background: linear-gradient(135deg, var(--color-gold) 0%, var(--color-cta-amber) 100%);
    color: var(--color-white);
    text-align: center;
    padding: 80px 20px;
}

.final-cta-title {
    font-size: 2.5rem;
    margin-bottom: 20px;
}

.final-cta-text {
    font-size: 1.3rem;
    margin-bottom: 35px;
    opacity: 0.95;
}

.final-cta-guarantee {
    margin-top: 25px;
    font-size: 1rem;
    opacity: 0.9;
}

/* ============================================
   Footer
   ============================================ */
.footer {
    background-color: var(--color-footer-navy);
    color: var(--color-white);
    padding: 50px 20px 20px;
}

.footer-content {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 40px;
    margin-bottom: 30px;
}

.footer-section h3 {
    color: var(--color-gold);
    margin-bottom: 15px;
    font-size: 1.2rem;
}

.footer-section p {
    margin-bottom: 8px;
    opacity: 0.9;
}

.footer-link {
    color: var(--color-white);
    transition: var(--transition-smooth);
}

.footer-link:hover {
    color: var(--color-gold);
}

.disclaimer {
    font-size: 0.85rem;
    opacity: 0.7;
    margin-top: 15px;
    font-style: italic;
}

.footer-bottom {
    text-align: center;
    padding-top: 20px;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    opacity: 0.8;
}

/* ============================================
   Bouton WhatsApp Sticky (Flottant)
   ============================================ */
.whatsapp-sticky {
    position: fixed;
    bottom: 25px;
    right: 25px;
    width: 65px;
    height: 65px;
    background: linear-gradient(135deg, #25D366 0%, #1ebe57 100%);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: 0 4px 15px rgba(37, 211, 102, 0.4);
    z-index: 1000;
    transition: var(--transition-smooth);
    animation: pulse 2s infinite;
}

.whatsapp-sticky:hover {
    transform: scale(1.1);
    box-shadow: 0 6px 20px rgba(37, 211, 102, 0.6);
}

/* ============================================
   Responsive Design - Mobile First
   ============================================ */

/* Tablettes */
@media (max-width: 768px) {
    .hero-title {
        font-size: 1.8rem;
    }

    .hero-subtitle {
        font-size: 1.4rem;
    }

    .hero-description {
        font-size: 1rem;
    }

    .about-content {
        grid-template-columns: 1fr;
    }

    .section-title {
        font-size: 1.8rem;
    }

    .steps {
        flex-direction: column;
    }

    .step-arrow {
        transform: rotate(90deg);
        margin: 10px 0;
    }

    .final-cta-title {
        font-size: 2rem;
    }

    .final-cta-text {
        font-size: 1.1rem;
    }
}

/* Mobile */
@media (max-width: 480px) {
    .header-content {
        justify-content: center;
    }

    .logo {
        font-size: 1.3rem;
    }

    .btn {
        padding: 10px 20px;
        font-size: 0.95rem;
    }

    .btn-large {
        padding: 14px 30px;
        font-size: 1rem;
    }

    .hero {
        min-height: 80vh;
        padding: 60px 15px;
    }

    .hero-title {
        font-size: 1.5rem;
    }

    .hero-subtitle {
        font-size: 1.2rem;
    }

    section {
        padding: 40px 15px;
    }

    .section-title {
        font-size: 1.6rem;
    }

    .features-grid,
    .services-grid,
    .testimonials-grid {
        grid-template-columns: 1fr;
    }

    .whatsapp-sticky {
        width: 60px;
        height: 60px;
        bottom: 20px;
        right: 20px;
    }

    .whatsapp-sticky svg {
        width: 28px;
        height: 28px;
    }
}

/* Accessibilité - Contraste AA */
@media (prefers-reduced-motion: reduce) {
    *,
    *::before,
    *::after {
        animation-duration: 0.01ms !important;
        animation-iteration-count: 1 !important;
        transition-duration: 0.01ms !important;
    }
}
